import React, { useEffect, useState, useMemo } from 'react'
import { useTable, useGlobalFilter } from 'react-table'
import GlobalFilter from '../../components/Contact/GlobalFilter'
import CreateOutlinedIcon from '@mui/icons-material/CreateOutlined';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import axios from 'axios'
import backendUrl from '../../const'

function ReactTable() {
    const [contacts, setContacts] = useState([])

    useEffect(() => {
        async function fetchData() {
            await axios.get(`${backendUrl}/contacts`)
                .then(resp => setContacts(resp.data))
                .catch(err=> console.log(err))
        }
        fetchData()
    }, [])
    const data = React.useMemo(
        () => [
            {
                name: 'ajay',
                email: 'avbbb@gmail.com',
                phone: '0978657809',
                actions: <>
                    <CreateOutlinedIcon color="primary" />
                    <DeleteOutlineOutlinedIcon sx={{ color: 'red' }} />
                </>,
            },
        ],
        []
    )

    const columns = React.useMemo(
        () => [
            {
                Header: 'Name',
                accessor: 'name',
            },
            {
                Header: 'Email',
                accessor: 'email',
            },
            {
                Header: 'Phone',
                accessor: 'phone',
            },
            {
                Header: 'Actions',
                accessor: 'actions',
            },
        ],
        []
    )

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        useFilters,
        state,
        setGlobalFilter,
    } = useTable({ columns, data }, useGlobalFilter)

    const { globalFilter } = state

    return (
        <section>
            <div className="container">
                <div className="contact-list-outer-box">
                    <div className="row">
                        <div className="col-12">
                            <h3 className="title">Contact List</h3>
                        </div>
                        <div className="search-box col-md-12 col-lg-4">
                            <div className="col-12">
                                <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter} />
                            </div>
                        </div>
                        <div className="col-md-12 col-lg-6"></div>
                        <div className="col-md-12 col-lg-2">
                            <button className='add-new'>Add new contact</button>
                        </div>
                        <div className="col-12 p-2 table-responsive">
                            <table {...getTableProps()} className='table table-bordered'>
                                <thead>
                                    {headerGroups.map(headerGroup => (
                                        <tr {...headerGroup.getHeaderGroupProps()}>
                                            {headerGroup.headers.map(column => (
                                                <th {...column.getHeaderProps()} >
                                                    {column.render('Header')}
                                                </th>
                                            ))}
                                        </tr>
                                    ))}
                                </thead>
                                <tbody {...getTableBodyProps()}>
                                    {rows.map(row => {
                                        prepareRow(row)
                                        return (
                                            <tr {...row.getRowProps()}>
                                                {row.cells.map(cell => {
                                                    return (
                                                        <td {...cell.getCellProps()} >
                                                            {cell.render('Cell')}
                                                        </td>
                                                    )
                                                })}
                                            </tr>
                                        )
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default ReactTable
