import SearchIcon from '@mui/icons-material/Search';
import { useEffect, useState } from 'react';
import CreateOutlinedIcon from '@mui/icons-material/CreateOutlined';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';

function Contact() {
    const contacts = [
        {
            "name": "ajay",
            "email": "aj@gmail.com",
            "number": "0909090909",
        },
        {
            "name": "akshay",
            "email": "akshy@gmail.com",
            "number": "989898998",
        },
        {
            "name": "abhi",
            "email": "abhi@gmail.com",
            "number": "7777687989",
        },
        {
            "name": "vipi",
            "email": "vipi@gmail.com",
            "number": "2342341234",
        },
    ];
    // to store keyword for searching
    let [key, setKey] = useState()

    // to store search result
    const [searchedTable, setSearchedTable] = useState([])

    // handle user search and store to key for searching
    const handleChange = (value) => {
        setKey(value)
    }

    useEffect(() => {
        // searching to the array using fillter and include method
        const result = contacts.filter((contact) => {
            const keyToLowerCase = key.toLowerCase()
            const keyToUpperCase = key.toUpperCase()
            if (contact.name.includes(keyToLowerCase) || contact.name.includes(keyToUpperCase)) {
                return contact;
            }
        });
        console.log(result);
    }, [key])

    return (
        <section>
            <div className="container">
                <div className="contact-list-outer-box">
                    <div className="row">
                        <div className="col-12">
                            <h3 className="title">Contact List</h3>
                        </div>
                        <div className="search-box col-md-12 col-lg-4">
                            <div className="col-12">
                                <SearchIcon />
                                <input
                                    type="text"
                                    placeholder="Search..."
                                    onChange={(e) => handleChange(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="col-md-12 col-lg-6"></div>
                        <div className="col-md-12 col-lg-2">
                            <button className='add-new'>Add new contact</button>
                        </div>
                        <div className="col-12 p-2 table-responsive">
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Number</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        contacts.map(contact => {
                                            return (
                                                <tr key={contact.name}>
                                                    <td>{contact.name}</td>
                                                    <td>{contact.email}</td>
                                                    <td>{contact.number}</td>
                                                    <td>
                                                        <CreateOutlinedIcon color="primary" />
                                                        <DeleteOutlineOutlinedIcon sx={{ color: 'red' }} />
                                                    </td>
                                                    <p>{key}</p>
                                                </tr>
                                            )
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Contact
