import React from 'react'
import { useTable, useGlobalFilter } from 'react-table'
import GlobalFilter from '../../components/Contact/GlobalFilter'

function ReactTable() {
    const data = React.useMemo(
        () => [
            {
                name: 'ajay',
                email: 'ajay@gmail.com',
                phone: '0978657809',
            },
            {
                name: 'vijay',
                email: 'ajay@gmail.com',
                phone: '0978657809',
            },
            {
                name: 'happ',
                email: 'ajay@gmail.com',
                phone: '0978657809',
            },
        ],
        []
    )

    const columns = React.useMemo(
        () => [
            {
                Header: 'Name',
                accessor: 'name',
            },
            {
                Header: 'Email',
                accessor: 'email',
            },
            {
                Header: 'Phone',
                accessor: 'phone',
            },
        ],
        []
    )

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        useFilters,
        state,
        setGlobalFilter,
    } = useTable({ columns, data }, useGlobalFilter)

    const { globalFilter } = state

    return (
        // <>
        //     <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter}/>


        // </>
        <section>
            <div className="container">
                <div className="contact-list-outer-box">
                    <div className="row">
                        <div className="col-12">
                            <h3 className="title">Contact List</h3>
                        </div>
                        <div className="search-box col-md-12 col-lg-4">
                            <div className="col-12">
                                <GlobalFilter />
                            </div>
                        </div>
                        <div className="col-md-12 col-lg-6"></div>
                        <div className="col-md-12 col-lg-2">
                            <button className='add-new'>Add new contact</button>
                        </div>
                        <div className="col-12 p-2 table-responsive">
                            <table {...getTableProps()}>
                                <thead>
                                    {headerGroups.map(headerGroup => (
                                        <tr {...headerGroup.getHeaderGroupProps()}>
                                            {headerGroup.headers.map(column => (
                                                <th {...column.getHeaderProps()} >
                                                    {column.render('Header')}
                                                </th>
                                            ))}
                                        </tr>
                                    ))}
                                </thead>
                                <tbody {...getTableBodyProps()}>
                                    {rows.map(row => {
                                        prepareRow(row)
                                        return (
                                            <tr {...row.getRowProps()}>
                                                {row.cells.map(cell => {
                                                    return (
                                                        <td {...cell.getCellProps()} >
                                                            {cell.render('Cell')}
                                                        </td>
                                                    )
                                                })}
                                            </tr>
                                        )
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default ReactTable
