import React, { useEffect, useState } from 'react'
import CreateOutlinedIcon from '@mui/icons-material/CreateOutlined'
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined'
import axios from 'axios'
import backendUrl from '../../const'
import Toastify from '../Toastify'

function ReactTable() {
    const [duplicateErrorMsg, setDuplicateErrorMsg] = useState(false)
    const [contactDeleted, setContactDeleted] = useState(false)
    const [contacts, setContacts] = useState([])
    const [addFormData, setAddFormData] = useState({
        name: '',
        email: '',
        phone: '',
    })

    // function for handling input change and storing new contact to state(addFormData)
    const handleAddFormChange = (event) => {
        event.preventDefault()

        const fieldName = event.target.getAttribute('name')
        const fieldValue = event.target.value

        // create new object and new key-value
        const newFormData = { ...addFormData }
        newFormData[fieldName] = fieldValue

        setAddFormData(newFormData)
    }

    // onSubmiting form newContact object created and store all contacts (new + old)
    // then store to database also
    const handleAddFormSubmit = (event) => {
        event.preventDefault()

        const newContact = {
            name: addFormData.name,
            email: addFormData.email,
            phone: addFormData.phone,
        }

        const newContacts = [...contacts, newContact]

        // store new contact to database
        const storeToDb = async () => {
            await axios.post(`${backendUrl}/create`, newContact)
                .then(resp => {
                    alert('contact saved')
                    setContacts(newContacts)
                })
                .catch(err => {
                    setDuplicateErrorMsg(true)
                })
        }
        storeToDb()
        setDuplicateErrorMsg(false)
    }

    // api call to backend and store to contact state
    useEffect(() => {
        async function fetchData() {
            await axios.get(`${backendUrl}/contacts`)
                .then(resp => setContacts(resp.data))
                .catch(err => console.log(err))
        }
        fetchData()
    }, [])

    // function handle delete contact 
    const handleDelete = (id) => {
        const isDelete = window.confirm("do you want to delete")
        if (isDelete) {
            async function deleteContact() {
                await axios.delete(`${backendUrl}/contact/${id}`)
                    .then(resp => {
                        setContacts(contacts.filter(contact => contact._id !== id))
                        setContactDeleted(true)
                    })
                    .catch(err => {
                        console.log(err)
                        setDuplicateErrorMsg(true)
                    })
                setContactDeleted(false)
            }
            deleteContact()
        }
    }

    return (
        <section>

            {
                contactDeleted && <Toastify message='Contact deleted' />
            }
            <div className="container">
                {
                    duplicateErrorMsg &&
                    <div className="container">
                        <div className="row">
                            <div className="col-12 mt-3">
                                <div class="container alert alert-danger" role="alert">
                                    Duplicate contact not allowed!
                                </div>
                            </div>
                        </div>
                    </div>
                }
                <div className="contact-list-outer-box">
                    <div className="row">
                        <div className="col-lg-8">
                            <h3 className="title">Contact List</h3>
                        </div>
                        <div className="search-box col-4">
                            <div className="col-lg-12">
                                <input type="text" placeholder='Search...' />
                            </div>
                        </div>
                        <div className="col-12 mt-4">
                            <h5>Add new contact</h5>
                            {/* form for creating new contacts */}
                            <form className='add-contact-form' onSubmit={handleAddFormSubmit}>
                                <input
                                    type="text"
                                    name='name'
                                    placeholder='Name'
                                    // required
                                    onChange={handleAddFormChange}
                                />
                                <input
                                    type="email"
                                    name='email'
                                    placeholder='Email'
                                    // required
                                    onChange={handleAddFormChange}
                                />
                                <input
                                    type="tel"
                                    name='phone'
                                    placeholder='Phone'
                                    // required
                                    onChange={handleAddFormChange}
                                />
                                <button type="submit" className='add-new'>Add new contact</button>
                            </form>
                        </div>
                        <div className="col-12 p-2 table-responsive mt-4">
                            <table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Phone</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {/* map through contacts to display to ui */}
                                    {
                                        contacts.map(contact => (
                                            <tr key={contact._id}>
                                                <td>{contact.name}</td>
                                                <td>{contact.email}</td>
                                                <td>{contact.phone}</td>
                                                <td>
                                                    <CreateOutlinedIcon onClick={() => console.log(contact)} style={{ color: "#028ae9", cursor: "pointer", marginRight: 20, }} />
                                                    <DeleteOutlineOutlinedIcon onClick={() => handleDelete(contact._id)} style={{ color: "red", cursor: "pointer", }} />
                                                </td>
                                            </tr>
                                        ))
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default ReactTable


