import React, { useEffect, useState } from 'react'
import CreateOutlinedIcon from '@mui/icons-material/CreateOutlined'
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined'
import axios from 'axios'
import backendUrl from '../../const'
import mockData from '../../mock-data.json'

function ReactTable() {
    const [contacts, setContacts] = useState(mockData)

    // api call to backend and store to contact state
    // useEffect(() => {
    //     async function fetchData() {
    //         await axios.get(`${backendUrl}/contacts`)
    //             .then(resp => setContacts(resp.data))
    //             .catch(err => console.log(err))
    //     }
    //     fetchData()
    // }, [])

    // function handle delete contact 
    const handleDelete = (id) =>{
        // confirm deletion
        const isDelete = window.confirm("do you want to delete")
        if(isDelete){
            async function deleteContact(){
                await axios.delete(`${backendUrl}/contact/${id}`)
                .then(resp=>console.log(resp))
                .catch(err => console.log(err))
            }
            deleteContact()
        }
    }

    return (
        <section>
            <div className="container">
                <div className="contact-list-outer-box">
                    <div className="row">
                        <div className="col-12">
                            <h3 className="title">Contact List</h3>
                        </div>
                        <div className="search-box col-md-12 col-lg-4">
                            <div className="col-12">
                                <input type="text" />search
                            </div>
                        </div>
                        <div className="col-md-12 col-lg-6"></div>
                        <div className="col-md-12 col-lg-2">
                            <button className='add-new'>Add new contact</button>
                        </div>
                        <div className="col-12 p-2 table-responsive">
                            <table className='table table-bordered'>
                                <thead>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    <th>Actions</th>
                                </thead>
                                <tbody>
                                    {/* map through contact state display to ui */}
                                    {
                                        contacts.map(contact => (
                                            <tr key={contact._id}>
                                                <td>{contact.name}</td>
                                                <td>{contact.email}</td>
                                                <td>{contact.phone}</td>
                                                <td>
                                                    <CreateOutlinedIcon onClick={()=>console.log(contact)} style={{color: "#028ae9",cursor: "pointer",marginRight:20,}}/>
                                                    <DeleteOutlineOutlinedIcon onClick={()=>handleDelete(contact._id)}  style={{color: "red",cursor: "pointer",}} />
                                                </td>
                                            </tr>
                                        ))
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default ReactTable
